import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import Title from './Title';
import Card from './Card';

const App = () => {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [nextLink, setNextLink] = useState(null);
  const [hasMore, setHasMore] = useState(true);

  const fetchRecipes = useCallback(async () => {
    if (loading) return;
    setLoading(true);

    try {
      const response = await axios.get('http://localhost:3001/api/data', {
        params: {
          nextLink: nextLink
        }
      });

      const newRecipes = response.data.recipes;
      const newNextLink = response.data.nextLink;

      setRecipes(prev => [...prev, ...newRecipes]);
      setNextLink(newNextLink);
      setHasMore(!!newNextLink);

      console.log('Fetched recipes:', newRecipes.length);
      console.log('Next link:', newNextLink);
    } catch (error) {
      console.error("Error fetching data:", error);
    } finally {
      setLoading(false);
    }
  }, [loading, nextLink]);

  // Initial load
  useEffect(() => {
    fetchRecipes();
  }, []);

  // Infinite scroll handler
  useEffect(() => {
    const handleScroll = () => {
      if (loading || !hasMore) return;

      const scrollPosition = window.innerHeight + document.documentElement.scrollTop;
      const pageHeight = document.documentElement.scrollHeight;
      const threshold = 200;

      if (pageHeight - scrollPosition < threshold) {
        console.log('Triggering fetch near bottom');
        fetchRecipes();
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [loading, hasMore, fetchRecipes]);

  return (
    <div>
      <Title />
      <div className="container">
        {recipes.map((recipe, index) => (
          <Card 
            key={`${recipe.id}-${index}`}
            id={recipe.id}
            image={recipe.image}
            title={recipe.title}
            calories={recipe.calories}
          />
        ))}
      </div>
      {loading && (
        <div style={{ 
          textAlign: 'center', 
          padding: '20px',
          fontSize: '18px',
          color: '#666'
        }}>
          Loading more recipes...
        </div>
      )}
      {!hasMore && recipes.length > 0 && (
        <div style={{ 
          textAlign: 'center', 
          padding: '20px',
          fontSize: '18px',
          color: '#666'
        }}>
          No more recipes to load
        </div>
      )}
    </div>
  );
};

export default App;
