import React, {useEffect, useState} from 'react'
import Image from './Image';
import Name from './Name';
import Calorie from './Calorie';
function Card(props){
    const [isLiked, setIsLiked] = useState(false);
    const [isLoading, setIsLoading] = useState(false);


    // Check initial status
    useEffect(() => {
        const checkLikeStatus = async () => {
            try {
                const response = await fetch(`/api/likes/check/${props.id}`, {
                    credentials: 'include'
                });
                const data = await response.json();
                setIsLiked(data.isLiked);
            } catch (error) {
                console.error('Error checking like status:', error);
            }
        };

        checkLikeStatus();
    }, [props.id]);


    const handleLike = (e) => {
        setIsLiked(!isLiked);
      };

    return <div>
        <div className="card flex items-stretch">
            <div className="max-w-sm h-full bg border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
                <Image img={props.image}/>
                <div className="p-5">
                    <Name food={props.title}/>
                    <div className="absolute middle-4 calorie">
                    <Calorie cal={props.calories}/>
                    </div>
                    <div className="absolute bottom-4 right-4">
                        <button onClick={handleLike} className="p-2 transition-colors duration-200 rounded-full">
                            <i className={`fa fa-heart text-xl transition-colors duration-200 ${isLiked ? 'text-red-500' : 'text-gray-600 dark:text-gray-400 hover:text-red-500'}`}></i>
                        </button>
                    </div>
                </div>
            </div>

        </div>{/*   jfj */}
    </div>  

}


export default Card;
