import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Title from './Title';
import Card from './Card';
const App = () => {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [nextLink, setNextLink] = useState(null);

  const fetchRecipes = async (link) =>{
    if(loading) return;  //prevent multiple calls
    setLoading(true);

    try{
      const response = await axios.get(link);
      setRecipes(prev => [...prev, ...response.data.recipes]);
      setNextLink(response.data.nextLink);
      console.log(response.data.recipes);
    } catch(error){
      console.log("Error fetching data", error);

    }finally{
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchRecipes('http://localhost:3001/api/data');
  }, []);

  const handleScroll = () => {
    if (
        window.innerHeight + document.documentElement.scrollTop >= 
        document.documentElement.offsetHeight - 200 && // Load more when near bottom
        nextLink // Ensure nextLink is available
    ) {
        fetchRecipes(nextLink);
    }
};

useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
        window.removeEventListener('scroll', handleScroll);
    };
}, [nextLink]); // Run this effect when nextLink changes



  return (
    <div>
      < Title/>
      <div className='container'>
      {recipes.map((recipe, index) => (
              <Card 
                key={index}
                id={index}
                image={recipe.image}
                title={recipe.title}
                calories={recipe.calories}
              />
            ))}      </div>
    </div>
  );
};

export default App;
