import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import Title from './Title';
import Card from './Card';

const App = () => {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [nextLink, setNextLink] = useState(null);
  const [hasMore, setHasMore] = useState(true);

  const fetchRecipes = useCallback(async (link) => {
    if (loading || !hasMore) return;
    setLoading(true);

    try {
      const response = await axios.get(`http://localhost:3001/api/data${link ? `?nextLink=${encodeURIComponent(link)}` : ''}`);

      setRecipes(prev => [...prev, ...response.data.recipes]);
      setNextLink(response.data.nextLink);
      setHasMore(!!response.data.nextLink);
    } catch (error) {
      console.error("Error fetching data:", error);
    } finally {
      setLoading(false);
    }
  }, [loading, hasMore]);

  // Initial load
  useEffect(() => {
    fetchRecipes();
  }, []);

  // Infinite scroll handler
  useEffect(() => {
    const handleScroll = () => {
      if (loading || !hasMore) return;

      // Check if we're near the bottom of the page
      const scrollPosition = window.innerHeight + window.scrollY;
      const pageHeight = document.documentElement.scrollHeight;
      const threshold = 200; // pixels from bottom

      if (pageHeight - scrollPosition < threshold) {
        fetchRecipes(nextLink);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [loading, hasMore, nextLink, fetchRecipes]);

  return (
    <div>
      <Title />
      <div className="container">
        {recipes.map((recipe, index) => (
          <Card 
            key={recipe.id || index}
            id={recipe.id}
            image={recipe.image}
            title={recipe.title}
            calories={recipe.calories}
          />
        ))}
      </div>
      {loading && (
        <div className="flex justify-center p-4">
          <div className="text-gray-600">Loading more recipes...</div>
        </div>
      )}
      {!hasMore && recipes.length > 0 && (
        <div className="flex justify-center p-4">
          <div className="text-gray-600">No more recipes to load</div>
        </div>
      )}
    </div>
  );
};

export default App;
