import React, { useEffect, useState } from 'react';
import Image from './Image';
import Name from './Name';
import Calorie from './Calorie';
import { X } from 'lucide-react';

function Card(props) {
    const [isLiked, setIsLiked] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [isExpanded, setIsExpanded] = useState(false);

    // Existing useEffect for like status...
    useEffect(() => {
        if (props.id) {
            const checkLikeStatus = async () => {
                try {
                    const response = await fetch(`http://localhost:3001/api/likes/check/${props.id}`, {
                        credentials: 'include',
                        headers: {
                            'Accept': 'application/json',
                        }
                    });
                    const data = await response.json();
                    setIsLiked(data.isLiked);
                } catch (error) {
                    console.error('Error checking like status:', error);
                }
            };

            checkLikeStatus();
            const fallbackCheck = setTimeout(() => {
                if (!isLiked && props.id) {
                    checkLikeStatus();
                }
            }, 500); 
        }
    }, [props.id]);

    const handleLike = async (e) => {
        e.stopPropagation();
        setIsLoading(true);

        try {
            const response = await fetch('http://localhost:3001/api/likes', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                body: JSON.stringify({ recipeId: props.id }),
                credentials: 'include'
            });            

            if (response.ok) {
                const data = await response.json();
                setIsLiked(data.isLiked);
            } else {
                console.error('Server returned error:', await response.text());
            }
        } catch (error) {
            console.error('Error toggling like:', error);
        } finally {
            setIsLoading(false);
        }
    };

    const handleClick = () => {
        setIsExpanded(!isExpanded);
    };

    return (
        <div className={`relative transition-all duration-300 ease-in-out ${
            isExpanded ? 'fixed top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 z-50' : ''
        }`}>
            <div 
                className={`card transition-all duration-300 ease-in-out ${
                    isExpanded ? 'bg-white dark:bg-gray-800 rounded-lg shadow-xl' : ''
                }`}
                onClick={handleClick}
            >
                <div className={`relative transition-all duration-300 ${
                    isExpanded ? 'w-[600px] max-h-[80vh] overflow-y-auto' : 'max-w-sm h-full'
                } bg border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700`}>
                    {isExpanded && (
                        <button 
                            onClick={(e) => {
                                e.stopPropagation();
                                setIsExpanded(false);
                            }}
                            className="absolute top-2 right-2 z-10 p-1 bg-gray-100 dark:bg-gray-700 rounded-full hover:bg-gray-200 dark:hover:bg-gray-600 transition-colors"
                        >
                            <X size={20} />
                        </button>
                    )}

                    <Image img={props.image}/>
                    <div className="p-5">
                        <Name food={props.title}/>

                        {isExpanded ? (
                            <div className="mt-4 space-y-4">
                                <div className="flex items-center justify-between">
                                    <div className="calorie">
                                        <Calorie cal={props.calories}/>
                                    </div>
                                    <button 
                                        onClick={handleLike}
                                        disabled={isLoading}
                                        className={`p-2 transition-colors duration-200 rounded-full 
                                            ${isLoading ? 'cursor-wait opacity-50' : ''}`}
                                        aria-label={isLiked ? 'Unlike recipe' : 'Like recipe'}
                                    >
                                        {isLoading ? (
                                            <div className="animate-spin h-5 w-5">
                                                <svg className="h-full w-full text-gray-400" viewBox="0 0 24 24">
                                                    <circle 
                                                        className="opacity-25" 
                                                        cx="12" 
                                                        cy="12" 
                                                        r="10" 
                                                        stroke="currentColor" 
                                                        strokeWidth="4"
                                                        fill="none"
                                                    />
                                                    <path 
                                                        className="opacity-75"
                                                        fill="currentColor"
                                                        d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                                                    />
                                                </svg>
                                            </div>
                                        ) : (
                                            <i className={`fa fa-heart text-xl 
                                                ${isLiked ? 'text-red-500' : 'text-gray-600 dark:text-gray-400 hover:text-red-500'}`}
                                            />
                                        )}
                                    </button>
                                </div>

                                {/* Additional recipe details */}
                                {props.ingredients && (
                                    <div>
                                        <h3 className="text-lg font-semibold mb-2">Ingredients</h3>
                                        <ul className="list-disc pl-5 space-y-1">
                                            {props.ingredients.map((ingredient, index) => (
                                                <li key={index}>{ingredient}</li>
                                            ))}
                                        </ul>
                                    </div>
                                )}

                                {props.instructions && (
                                    <div>
                                        <h3 className="text-lg font-semibold mb-2">Instructions</h3>
                                        <ol className="list-decimal pl-5 space-y-2">
                                            {props.instructions.map((step, index) => (
                                                <li key={index}>{step}</li>
                                            ))}
                                        </ol>
                                    </div>
                                )}
                            </div>
                        ) : (
                            <>
                                <div className="absolute middle-4 calorie">
                                    <Calorie cal={props.calories}/>
                                </div>
                                <div className="absolute bottom-4 right-4">
                                    <button 
                                        onClick={handleLike}
                                        disabled={isLoading}
                                        className={`p-2 transition-colors duration-200 rounded-full 
                                            ${isLoading ? 'cursor-wait opacity-50' : ''}`}
                                        aria-label={isLiked ? 'Unlike recipe' : 'Like recipe'}
                                    >
                                        {isLoading ? (
                                            <div className="animate-spin h-5 w-5">
                                                <svg className="h-full w-full text-gray-400" viewBox="0 0 24 24">
                                                    <circle 
                                                        className="opacity-25" 
                                                        cx="12" 
                                                        cy="12" 
                                                        r="10" 
                                                        stroke="currentColor" 
                                                        strokeWidth="4"
                                                        fill="none"
                                                    />
                                                    <path 
                                                        className="opacity-75"
                                                        fill="currentColor"
                                                        d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                                                    />
                                                </svg>
                                            </div>
                                        ) : (
                                            <i className={`fa fa-heart text-xl 
                                                ${isLiked ? 'text-red-500' : 'text-gray-600 dark:text-gray-400 hover:text-red-500'}`}
                                            />
                                        )}
                                    </button>
                                </div>
                            </>
                        )}
                    </div>
                </div>
            </div>

            {/* Overlay background */}
            {isExpanded && (
                <div 
                    className="fixed inset-0 bg-black bg-opacity-50 z-40"
                    onClick={() => setIsExpanded(false)}
                />
            )}
        </div>
    );
}

export default Card;
