import React, {useState} from 'react'
import Image from './Image';
function Card(props){
    const [isLiked, setIsLiked] = useState(false);

    const handleLike = () => {
        setIsLiked(!isLiked);
      };

    return <div>
        <div className="card">
            <div className="max-w-sm bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
                <Image img={props.Image}/>
                <div className="p-5">
                    <a href="#"><h5 className="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">Noteworthy technology acquisitions 2021</h5></a>
                    <p className="mb-3 font-normal text-gray-700 dark:text-gray-400">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Duis ac condimentum ipsum. In lobortis libero urna, id commodo urna efficitur ut.</p>
                    <div className="flex justify-end">
                        <button onClick={handleLike} className="p-2 transition-colors duration-200 rounded-full">
                            <i className={`fa fa-heart text-xl transition-colors duration-200 ${isLiked ? 'text-red-500' : 'text-gray-600 dark:text-gray-400 hover:text-red-500'}`}></i>
                        </button>
                    </div>
                </div>
            </div>

        </div>{/*   jfj */}
    </div>  

}


export default Card;
