import React, { useEffect, useState } from 'react';
import Image from './Image';
import Name from './Name';
import Calorie from './Calorie';

function Card(props) {
    const [isLiked, setIsLiked] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [isExpanded, setIsExpanded] = useState(false);

    // Existing useEffect and handleLike logic remains the same...
    useEffect(() => {
        if (props.id) {
            const checkLikeStatus = async () => {
                try {
                    const response = await fetch(`http://localhost:3001/api/likes/check/${props.id}`, {
                        credentials: 'include',
                        headers: {
                            'Accept': 'application/json',
                        }
                    });
                    const data = await response.json();
                    setIsLiked(data.isLiked);
                } catch (error) {
                    console.error('Error checking like status:', error);
                }
            };

            checkLikeStatus();
            const fallbackCheck = setTimeout(() => {
                if (!isLiked && props.id) {
                    checkLikeStatus();
                }
            }, 500); 
        }
    }, [props.id]);

    const handleLike = async (e) => {
        e.stopPropagation(); // Prevent card expansion when clicking like button
        setIsLoading(true);

        try {
            const response = await fetch('http://localhost:3001/api/likes', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                body: JSON.stringify({ recipeId: props.id }),
                credentials: 'include'
            });            

            if (response.ok) {
                const data = await response.json();
                setIsLiked(data.isLiked);
            } else {
                console.error('Server returned error:', await response.text());
            }
        } catch (error) {
            console.error('Error toggling like:', error);
        } finally {
            setIsLoading(false);
        }
    };

    const handleClick = () => {
        setIsExpanded(!isExpanded);
    };

    const handleCloseModal = (e) => {
        e.stopPropagation();
        setIsExpanded(false);
    };

    return (
        <>
            <div className="card flex items-stretch" onClick={handleClick}>
                <div className="max-w-sm h-full bg border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
                    <Image img={props.image}/>
                    <div className="p-5"> 
                        <Name food={props.title}/>
                        <div className="absolute middle-4 calorie">
                            <Calorie cal={props.calories}/>
                        </div>
                        <div className="absolute bottom-4 right-4">
                            <button 
                                onClick={handleLike}
                                disabled={isLoading}
                                className={`p-2 transition-colors duration-200 rounded-full 
                                    ${isLoading ? 'cursor-wait opacity-50' : ''}`}
                                aria-label={isLiked ? 'Unlike recipe' : 'Like recipe'}
                            >
                                {isLoading ? (
                                    <div className="animate-spin h-5 w-5">
                                        <svg className="h-full w-full text-gray-400" viewBox="0 0 24 24">
                                            <circle 
                                                className="opacity-25" 
                                                cx="12" 
                                                cy="12" 
                                                r="10" 
                                                stroke="currentColor" 
                                                strokeWidth="4"
                                                fill="none"
                                            />
                                            <path 
                                                className="opacity-75"
                                                fill="currentColor"
                                                d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                                            />
                                        </svg>
                                    </div>
                                ) : (
                                    <i className={`fa fa-heart text-xl 
                                        ${isLiked ? 'text-red-500' : 'text-gray-600 dark:text-gray-400 hover:text-red-500'}`}
                                    />
                                )}
                            </button>
                        </div>
                    </div>
                </div>
            </div>

            {/* Modal/Expanded View */}
            {isExpanded && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50" onClick={handleCloseModal}>
                    <div className="bg-white dark:bg-gray-800 rounded-lg p-6 max-w-2xl w-full m-4" onClick={e => e.stopPropagation()}>
                        <div className="relative">
                            <button 
                                onClick={handleCloseModal}
                                className="absolute -top-2 -right-2 bg-gray-200 dark:bg-gray-700 rounded-full p-2 hover:bg-gray-300 dark:hover:bg-gray-600"
                            >
                                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                                </svg>
                            </button>

                            <div className="flex flex-col md:flex-row gap-6">
                                <div className="md:w-1/2">
                                    <Image img={props.image} />
                                </div>
                                <div className="md:w-1/2">
                                    <h2 className="text-2xl font-bold mb-4">{props.title}</h2>
                                    <div className="mb-4">
                                        <span className="font-semibold">Calories:</span> {props.calories}
                                    </div>

                                    {/* Additional information sections */}
                                    {props.ingredients && (
                                        <div className="mb-4">
                                            <h3 className="text-lg font-semibold mb-2">Ingredients:</h3>
                                            <ul className="list-disc pl-5">
                                                {props.ingredients.map((ingredient, index) => (
                                                    <li key={index}>{ingredient}</li>
                                                ))}
                                            </ul>
                                        </div>
                                    )}

                                    {props.instructions && (
                                        <div className="mb-4">
                                            <h3 className="text-lg font-semibold mb-2">Instructions:</h3>
                                            <ol className="list-decimal pl-5">
                                                {props.instructions.map((step, index) => (
                                                    <li key={index} className="mb-2">{step}</li>
                                                ))}
                                            </ol>
                                        </div>
                                    )}

                                    {props.nutritionInfo && (
                                        <div className="mb-4">
                                            <h3 className="text-lg font-semibold mb-2">Nutrition Information:</h3>
                                            <div className="grid grid-cols-2 gap-2">
                                                {Object.entries(props.nutritionInfo).map(([key, value]) => (
                                                    <div key={key} className="flex justify-between">
                                                        <span className="font-medium">{key}:</span>
                                                        <span>{value}</span>
                                                    </div>
                                                ))}
                                            </div>
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </>
    );
}

export default Card;
