import React, {useEffect, useState} from 'react'
import Image from './Image';
import Name from './Name';
import Calorie from './Calorie';
import {motion} from "framer-motion"

function Card(props){
    const [isLiked, setIsLiked] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [isFlipped, setIsFlipped] = useState(false);
    const [isAnimated, setIsAnimated] = useState(false);

    // Check initial status
    useEffect(() => {
        if (props.id) {  // Make sure props.id is not undefined or empty
            const checkLikeStatus = async () => {
                try {
                    const response = await fetch(`http://localhost:3001/api/likes/check/${props.id}`, {
                        credentials: 'include',
                        headers: {
                            'Accept': 'application/json',
                        }
                    });
                    const data = await response.json();
                    setIsLiked(data.isLiked);
                } catch (error) {
                    console.error('Error checking like status:', error);
                }
            };

            checkLikeStatus();  // Trigger the like status check
            const fallbackCheck = setTimeout(() => {
                if (!isLiked && props.id) {
                    checkLikeStatus();
                }
            }, 500); 
        }
    }, [props.id]);  // Rerun the effect when props.id changes


    const handleLike = async (e) => {
        setIsLoading(true);

        try {
            const response = await fetch('http://localhost:3001/api/likes', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                },
                body: JSON.stringify({ recipeId: props.id }),
                credentials: 'include'
            });            

            if (response.ok) {
                const data = await response.json();
                setIsLiked(data.isLiked);  // Use the server response to set the state
                console.log('Like status updated:', data.isLiked);  // Debug log
            } else {
                console.error('Server returned error:', await response.text());
            }
        } catch (error) {
            console.error('Error toggling like:', error);
        } finally {
            setIsLoading(false);
        }
    };

    function handleClick(){
        if(!isAnimated){
            setIsFlipped(!isFlipped);
            setIsAnimated(true);
        }
    }
    return (
        <div>
            <div className="card flex items-stretch">
                <div className='flip-card' onClick={handleClick}>    
                    <div className="max-w-sm h-full bg border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
                        <Image img={props.image}/>
                        <div className="p-5"> 
                            <Name food={props.title}/>
                            <div className="absolute middle-4 calorie">
                                <Calorie cal={props.calories}/>
                            </div>
                            <div className="absolute bottom-4 right-4">
                                <button 
                                    onClick={handleLike}
                                    disabled={isLoading}
                                    className={`p-2 transition-colors duration-200 rounded-full 
                                        ${isLoading ? 'cursor-wait opacity-50' : ''}`}
                                    aria-label={isLiked ? 'Unlike recipe' : 'Like recipe'}
                                >
                                    {isLoading ? (
                                        <div className="animate-spin h-5 w-5">
                                            <svg className="h-full w-full text-gray-400" viewBox="0 0 24 24">
                                                <circle 
                                                    className="opacity-25" 
                                                    cx="12" 
                                                    cy="12" 
                                                    r="10" 
                                                    stroke="currentColor" 
                                                    strokeWidth="4"
                                                    fill="none"
                                                />
                                                <path 
                                                    className="opacity-75"
                                                    fill="currentColor"
                                                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                                                />
                                            </svg>
                                        </div>
                                    ) : (
                                        <i className={`fa fa-heart text-xl 
                                            ${isLiked ? 'text-red-500' : 'text-gray-600 dark:text-gray-400 hover:text-red-500'}`}
                                        />
                                    )}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>  
    );
}

export default Card;
